echo "Run it as localuser"

mkdir ~/.ssh
echo "" | base64 -d > ~/.ssh/id_rsa
echo "" | base64 -d > ~/.ssh/id_rsa.pub
cat ~/.ssh/id_rsa.pub > ~/.ssh/authorized_keys


sudo sed -i 's/#\?PubkeyAuthentication.*/PubkeyAuthentication yes/' /etc/ssh/ssh_config
sudo sed -i 's/#\?AuthorizedKeysFile.*/AuthorizedKeysFile \/path\/to\/authorized_keys/' /etc/ssh/ssh_config
